def go_to_frame(event):

      global max_number_frames
      global loaded_image_tag
      global cap
      global frame_counter
      global img_res
      global img_gray_res
      global resized_img_width
      global resized_img_height

      go_to_frame_value  = int(enter_go_to_frame.get())

      if ((go_to_frame_value>=0) & (go_to_frame_value < max_number_frames)):

           frame_counter =  go_to_frame_value
           if(cap.isOpened()):
     
              cap.set(cv2.CAP_PROP_POS_FRAMES, frame_counter)
              ret, frame = cap.read()
              imgRGB = cv2.cvtColor(frame, cv2.COLOR_BGR2RGB)
              gray = cv2.cvtColor(frame, cv2.COLOR_BGR2GRAY)
              img = Image.fromarray(imgRGB)
              img_gray = Image.fromarray(gray)
              adjust_image_to_window(img_width=img.size[0], img_height=img.size[1])
              img_res = img.resize((resized_img_width, resized_img_height), Image.ANTIALIAS)
              img_gray_res = img_gray.resize((resized_img_width, resized_img_height), Image.ANTIALIAS)
              imgtk = ImageTk.PhotoImage(img_res)
              tlo.image = imgtk
    # tlo.create_image(0, 0, image=imgtk, anchor=NW)

              tlo.itemconfig(loaded_image_tag, image = imgtk)
              label_current_frame.configure(text='current frame={} / ({})'.format(frame_counter,max_number_frames))    



