def adjust_image_to_window(img_width, img_height):
   global tlo_width
   global tlo_height
   global org_img_width
   global org_img_height
   global resized_img_width
   global resized_img_height
   global image_width
   global image_height

   

   if(org_img_width!=img_width or org_img_height!=img_height):
      org_img_width = img_width
      org_img_height = img_height

      div_scale = 1.0
      tlo_wh_prop = tlo_width/tlo_height
      img_wh_prop = org_img_width/org_img_height

      if(tlo_wh_prop>=img_wh_prop): 
         #skalowanie do wysokosci
         if(tlo_height<org_img_height):
            div_scale = org_img_height/tlo_height      
      else:
         #skalowanie do szerokosci
         if(tlo_width<org_img_width):
          div_scale = org_img_width/tlo_width

      if(div_scale!=1.0):
         resized_img_width =  int (org_img_width/div_scale)
         resized_img_height = int (org_img_height/div_scale)
      else:
         resized_img_width = org_img_width    
    
         resized_img_height = org_img_height

      image_width = resized_img_width
      image_height = resized_img_height

